mapper.select:

SELECT * FROM( SELECT rownum rn, a.* FROM(
SELECT * FROM COURSE 

ORDER BY COU_CODE

) a WHERE rownum <= #{last} ) b WHERE rn >= #{first}

SELECT * FROM ( SELECT ROWNUM AS rnum, t.* FROM (
SELECT * FROM ( select 'a' gb , TO_NUMBER(cou_code) AS cou_code, cou_name, region_code AS region_code , img_gro_code FROM COURSE
union all
select 'm' gb , my_course_code, my_cou_name, my_cou_state, ( SELECT t.img_gro_code from my_course_free c join trip t on c.trip_unique_number = t.trip_unique_number where my_course_code = m.my_course_code AND cou_ord = '1' AND rownum = 1 ) img from my_course m WHERE m.my_cou_state = '1' ORDER BY gb desc )
) t WHERE ROWNUM <= #{last} ) WHERE rnum >= #{first}
