mapper.update:
	  update my_course
      set my_cou_content = #{myCouContent}
      where my_course_code = #{myCourseCode}

UPDATE MY_COURSE
SET MY_COURSE_CODE = MY_COURSE_CODE - 1
WHERE MY_COURSE_CODE > #{myCourseCode}

UPDATE MY_COURSE
SET MY_COU_STATE = #{myCouState}
WHERE MY_COURSE_CODE = #{myCourseCode}
