mapper.select:
		select * from ( select rownum rn, a.* from(
		SELECT * FROM notice order
		by notice_code desc

 ) a  where rownum <= #{last} ) b  where rn  >= #{first}

mapper.update:
update notice
set notice_Hit = notice_Hit + 1
where notice_Code = #{noticeCode}

UPDATE NOTICE
NOTICE_TITLE = #{noticeTitle}, NOTICE_CONTENT = #{noticeContent}, NOTICE_MOD_DATE = sysdate
WHERE NOTICE_CODE = #{noticeCode}
